            double d = getSimpleNumber(key);
            if (!Double.isNaN(d)) {
              cc.addNumber(d);
            } else {
              addExpr(c, 1, Context.OTHER);
            }
          }
          add(":");
          addExpr(c.getFirstChild(), 1, Context.OTHER);
