                    Element node = furthestBlock;
                    Element lastNode = furthestBlock;
                    INNER:
                    for (int j = 0; j < 3; j++) {
                        if (tb.onStack(node))
                            node = tb.aboveOnStack(node);
                        if (!tb.isInActiveFormattingElements(node)) { // note no bookmark check
