                tb.reconstructFormattingElements();
                // todo: handle A start tag whose tag name is "math" (i.e. foreign, mathml)
                tb.insert(startTag);
            } else if (name.equals("svg")) {
                tb.reconstructFormattingElements();
                // todo: handle A start tag whose tag name is "svg" (xlink, svg)
                tb.insert(startTag);
